mapper.insert:

		INSERT INTO file_info(
        	   file_id
        	 , file_number
        	 , file_name
        	 , file_path
        	 , file_size
        	 , file_extension
        	 , create_id
        	 , create_date
        	 , update_id
        	 , update_date
        ) VALUES (
             #{fileId}
           , #{fileNumber}
           , #{fileName}
           , #{uploadFilePath}
           , #{fileSize}
           , #{fileExtension}



		        		 , #{createId} 


		        		 , 0 



           , NOW()



	        		 , #{updateId} 


	        		 , 0 



           , NOW()
        )

mapper.update:

		UPDATE file_info
		   SET file_name = #{fileName}
        	 , file_path = #{uploadFilePath}
        	 , file_size = #{fileSize}
        	 , file_extension = #{fileExtension}
        	 , update_id = #{updateId}
        	 , update_date = NOW()
         WHERE file_id = #{fileId}
           AND file_number = #{fileNumber}

mapper.delete:

DELETE FROM file_info
WHERE file_id = #{fileId}
AND file_path = #{filePath}
