mapper.insert:

INSERT INTO user_info (
      email
	, password
	, name_first
	, name_last
	, user_type
	, division_code
	, country_code
	, hp_name 
	, hp_zipcode
	, hp_add
	, file_id
	, request_date
	, state_code
	, consent_yn
	, create_id
	, create_date
	, update_id
	, update_date
) VALUES (
      #{email}
	, #{password , jdbcType=VARCHAR}
	, #{nameFirst}
	, #{nameLast}
	, #{userType}
	, #{divisionCode}
	, #{countryCode}
	, #{hpName}
	, #{hpZipcode}
	, #{hpAdd}
	, #{fileId}
	, NOW()
	, #{stateCode}
	, #{consentYn}
	, #{createId}
	, NOW()
	, #{updateId}
	, NOW()
)

mapper.select:

SELECT COUNT(*) 
  FROM user_info 
 WHERE email = #{email}

SELECT seq , email , password , name_first as nameFirst , name_last as nameLast , user_type as userType , division_code as divisionCode , country_code as countryCode , file_id as fileId , request_date as requestDate , confirm_id as confirmId , confirm_date as confirmDate , state_code as stateCode , consent_yn as consentYn , remark , create_id as createId , create_date as createDate , update_id as updateId , update_date as updateDate , hp_name as hpName , hp_zipcode as hpZipcode , hp_add as hpAdd FROM user_info
WHERE email = #{email}
WHERE seq = #{seq}

mapper.update:

UPDATE user_info
   SET password = #{password , jdbcType=VARCHAR}
       , update_date = NOW()
       , update_id = #{seq}
 WHERE email = #{email}


UPDATE user_info
SET delete_yn = 'Y'
	, update_data = NOW()
	, update_id = #{updateId}
WHERE seq = #{userSeq}


