mapper.insert:

INSERT INTO SAM_ACHIEVEMENT(
       NUM
     , YYYY
     , MM
     , VALUE
     , ETC
) VALUES (
       #{num}
     , #{yyyy}
     , #{mm}
     , #{value, jdbcType=VARCHAR}
     , #{etc, jdbcType=VARCHAR}
)

mapper.update:

 UPDATE SAM_ACHIEVEMENT
   SET NUM = #{num}


 , VALUE = #{value} 


 , ETC = #{etc}  

WHERE NUM = #{num}
  AND YYYY = #{yyyy}
  AND MM = #{mm}

mapper.delete:

DELETE
  FROM SAM_ACHIEVEMENT
 WHERE NUM = #{num}


 AND YYYY = #{yyyy} 


 AND MM = #{mm}
